    try {
        if (!con3.isClosed()) {

            String insertSql3 = query;

            PreparedStatement stmtInsert3 = con3.prepareStatement(insertSql3);            
            ////Execute SQL statement
            stmtInsert3.executeUpdate();

        }

    } catch (Exception ex) {
        LogUtil.error("Dictionnaire signalement", ex, "Error storing using jdbc");
    } finally {
