        }
    }
}



public void generateDoc(String body_src){
    try{
        LogUtil.info("IN METHOD : ", "POST INFO");


        HttpClient client = HttpClientBuilder.create().build();


        String completeUrl = "http://127.0.0.1:5000/generate";
        LogUtil.info("URL URL", completeUrl);
        HttpPost httpPost = new HttpPost(completeUrl);

        httpPost.addHeader("Accept", "application/json");
        httpPost.addHeader("Content-type", "application/json");
        httpPost.addHeader("Accept-Charset", "utf-8");

        String body = body_src;



        LogUtil.info("BODY", body);

        StringEntity stringEntity = new StringEntity(body, "UTF-8");
        stringEntity.setContentType("application/json");

        httpPost.setEntity(stringEntity);

        HttpResponse response = client.execute(httpPost);
        LogUtil.info("HTTPPOST", response.toString());
        int statusCode = response.getStatusLine().getStatusCode();
        LogUtil.info("STATUS CODE", statusCode+"");

    } catch (Exception ex) {
            System.out.println(ex);
    }
}







