                    stmtUPDATE.setString(3, column3);
                    stmtUPDATE.setString(4, column4);
                    stmtUPDATE.setString(5, column5);
                    stmtUPDATE.setString(6, column6);

                    stmtUPDATE.executeUpdate();
                }else{
                    String insertSql = "INSERT INTO app_fd_info_personnel (id, c_idPhotot, c_idTele, c_idAdress, c_idLinkedin, c_idDescProfil, c_forieginKey) VALUES (?,?,?,?,?,?,?);";
                    PreparedStatement stmtInsert = con.prepareStatement(insertSql);

