import org.joget.apps.form.service.FormUtil;
import org.joget.commons.util.UuidGenerator;

public saveLangue(Element element, FormRowSet rows, FormData formData) {

    Connection con = null;

    try {

        DataSource ds = (DataSource) AppUtil.getApplicationContext().getBean("setupDataSource");

        con = ds.getConnection();

        if(!con.isClosed()) {

            UuidGenerator uuid = UuidGenerator.getInstance();



            Iterator i= rows.iterator();
            while (i.hasNext()) {
                FormRow row = (FormRow) i.next();
                String pId = uuid.getUuid();
                String column1 = row.get("idLangue");
                String column2 = row.get("idNiveau");
                String column3 = row.get("idFKuser");
                String column4 = row.get("idGenerExperience");

                String x= "SELECT id FROM app_fd_Langue WHERE id=?";
                PreparedStatement stmt = con.prepareStatement(x);
                stmt.setString(1, column4);
                ResultSet rs = stmt.executeQuery();

                Boolean isExist = false;
                if (rs.next()) {
                    isExist = true;
                }

                if (isExist) {
                    String  updateSql = "UPDATE app_fd_Langue SET c_idLangue = ?, c_idNiveau=?  WHERE id = ?";
                    PreparedStatement stmtUPDATE = con.prepareStatement(updateSql);

                    stmtUPDATE.setString(1, column1);
                    stmtUPDATE.setString(2, column2);
                    stmtUPDATE.setString(3, column4);

                    stmtUPDATE.executeUpdate();
                }else{

                String insertSql = "INSERT INTO app_fd_Langue (id,c_idLangue,c_idNiveau,c_idFKuser) VALUES (?,?,?,?);";
                PreparedStatement stmtInsert = con.prepareStatement(insertSql);

                stmtInsert.setString(1, pId);
                stmtInsert.setString(2, column1);
                stmtInsert.setString(3, column2);
                stmtInsert.setString(4, column3);

                stmtInsert.executeUpdate();

                    }
            }
        }
    } catch (Exception ex) {
        LogUtil.error("Your App/Plugin Name", ex, "Error storing using jdbc");
    } finally {
        try {
            if (con != null) {
                con.close();
            }
        } catch (Exception ex) {
            LogUtil.error("Your App/Plugin Name", ex, "Error closing the jdbc connection");
        }
    }
}

saveLangue(element, rows, formData);
