import org.joget.commons.util.UuidGenerator;

public saveInfoPersonnel(Element element, FormRowSet rows, FormData formData) {

    Connection con = null;

    try {

        DataSource ds = (DataSource) AppUtil.getApplicationContext().getBean("setupDataSource");

        con = ds.getConnection();

        if(!con.isClosed()) {

            UuidGenerator uuid = UuidGenerator.getInstance();
            String fk="#currentUser.username#";

            String x= "SELECT id FROM app_fd_info_personnel WHERE c_forieginKey=?";
            PreparedStatement stmt = con.prepareStatement(x);
            stmt.setString(1, fk);
            ResultSet rs = stmt.executeQuery();

            Boolean isExist = false;
            if (rs.next()) {
                isExist = true;
            }

            Iterator i= rows.iterator();
            while (i.hasNext()) {
            FormRow row = (FormRow) i.next();
                if (isExist) {
                    String gridColumn1 = row.get("idPhotot");
                    String gridColumn2 = row.get("idTele");
                    String gridColumn3 = row.get("idAdress");
                    String gridColumn4 = row.get("idLinkedin");
                    String gridColumn5 = row.get("idDescProfil");
                    String gridColumn6 = rs.getString("id");

                    String  updateSql = "UPDATE app_fd_info_personnel SET c_idPhotot = ?, c_idTele = ?, c_idAdress = ?, c_idLinkedin = ?, c_idDescProfil = ? WHERE id = ?";
                    PreparedStatement stmtUPDATE = con.prepareStatement(updateSql);
                    stmtUPDATE.setString(1, gridColumn1);
                    stmtUPDATE.setString(2, gridColumn2);
                    stmtUPDATE.setString(3, gridColumn3);
                    stmtUPDATE.setString(4, gridColumn4);
                    stmtUPDATE.setString(5, gridColumn5);
                    stmtUPDATE.setString(6, gridColumn6);

                    stmtUPDATE.executeUpdate();
                }else{
                    String pId = uuid.getUuid();
                    String gridColumn1 = row.get("idPhotot");
                    String gridColumn2 = row.get("idTele");
                    String gridColumn3 = row.get("idAdress");
                    String gridColumn4 = row.get("idLinkedin");
                    String gridColumn5 = row.get("idDescProfil");
                    String gridColumn6 = row.get("forieginKey");

                    String insertSql = "INSERT INTO app_fd_info_personnel (id, c_idPhotot, c_idTele, c_idAdress, c_idLinkedin, c_idDescProfil, c_forieginKey) VALUES (?,?,?,?,?,?,?);";
                    PreparedStatement stmtInsert = con.prepareStatement(insertSql);

                    stmtInsert.setString(1, pId);
                    stmtInsert.setString(2, gridColumn1);
                    stmtInsert.setString(3, gridColumn2);
                    stmtInsert.setString(4, gridColumn3);
                    stmtInsert.setString(5, gridColumn4);
                    stmtInsert.setString(6, gridColumn5);
                    stmtInsert.setString(7, gridColumn6);
                    stmtInsert.executeUpdate();

                    }
            }
       }
    } catch (Exception ex) {
        LogUtil.error("Your App/Plugin Name", ex, "Error storing using jdbc");
    } finally {
        try {
            if (con != null) {
                con.close();
            }
        } catch (Exception ex) {
            LogUtil.error("Your App/Plugin Name", ex, "Error closing the jdbc connection");
        }
    }
}

saveInfoPersonnel(element, rows, formData);

